 (650,1000,'OFC_letters',4,NULL,'ADD',4,'http://framework.krrtf.ir/office/start.php?SystemID=4',NULL,'46.100.132.107','2016-02-26 08:10:27','insert into OFC_letters(LetterType,LetterTitle,LetterDate,RegDate,PersonID,context) values (\'INNER\',\'تحویل سیستم اتوماسیون اداری\',now(),now(),\'1000\',\'<p>باسلام</p>\r\n\r\n<p>تغییرات زیر در سیستم اتوماسیون اداری اعمال گردید:</p>\r\n\r\n<ul>\r\n	<li>امکان حذف نامه از کارتابل نامه های رسیده و همچنین امکان اضافه مجدد آن به کارتابل</li>\r\n	<li>تعیین فرستنده/گیرنده برای نامه های وارده و صادره</li>\r\n	<li>نمایش شماره نامه بر اساس سال-شماره بارکد نامه</li>\r\n	<li>امکان تعیین امضا کننده نامه</li>\r\n	<li>امکان امضای نامه برای امضا کننده</li>\r\n	<li>چاپ نامه روی برگه سربرگ دار و برگه سفید هم A4 و هم A5</li>\r\n	<li>امکان چاپ نامه با امضا یا بدون امضا</li>\r\n</ul>\r\n\')'),
